// client/src/components/TextContainer/TextContainer.js

import React from 'react';

import onlineIcon from '../icons/onlineIcon.png';

import './TextContainer.css';

const TextContainer = ({ users }) => (
    <div className="textContainer">
        <div>
            <h1>Realtime Chat Application <span role="img" aria-label="emoji">üí¨</span></h1>
            <h2>Crated with React, Express, Node, Socket.IO<span role="img" aria-label="emoji">‚ù§Ô∏è</span></h2>
            <h2>Try it out right now!<span role="img" aria-label="emoji">‚¨ÖÔ∏è</span></h2>
        </div>
        {
            users ? (
                <div>
                    <h1>People currently chatting:</h1>
                    <div className="activeContainer">
                        <h2>
                            {
                                users.map(({name}) => (
                                    <div key={name} className="activeItem">
                                        {name}
                                        <img alt="Online Icon" src={onlineIcon} />
                                    </div>
                                ))
                            }
                        </h2>
                    </div>
                </div>
            ) : null
        }
    </div>
);

export default TextContainer;
